sys_ui_script.script:
var x_gmeps_itsm_servi = x_gmeps_itsm_servi || {};

x_gmeps_itsm_servi.GServiceScrollWidth = (function() {
	"use strict";

	/* set your private variables and functions here. For example: 
	var privateVar = 0; 
	function private_function() {
		return ++privateVar;
	}
*/

	var checkScrollBars = function(){
		var b = $('body');
		var normalw = 0;
		var scrollw = 0;
		if(b.prop('scrollHeight')>b.height()){
			normalw = window.innerWidth;
			scrollw = normalw - b.width();
			$('.container').css({marginRight:'-'+scrollw+'px'});
		}
	};

	/* Share variables between multiple UI scripts by adding them to your scope object. For example: 
	x_gmeps_itsm_servi.sharedVar = 0; 

 Then access them in your scripts the same way. For example: 
	function get_shared() {
		return x_gmeps_itsm_servi.sharedVar;
	}
*/

	return {

		/* set your public API here. For example:
		incrementAndReturnPrivateVar: function() {
			return private_function();
		},
*/
		type:  "GServiceScrollWidth"
	};
})();
