sp_rectangle_menu_item.record_script:
// maximum number of entries in this Menu
var max = 9999;

var t = data;  // shortcut
t.items = [];

var u = gs.getUser().getID();

// use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({'table':'incident','filter':'active=true^opened_by=' + u});
t.record_watchers.push({'table':'sc_req_item','filter':'active=true^opened_by=' + u});

//Incidents
var z = new GlideRecord('incident');
//z.addActiveQuery();
z.addQuery('opened_by', gs.getUserID());
z.orderByDesc('sys_updated_on');
z.query();
while (z.next()) {
  if (!z.canRead())
    continue;

  var a = {};
  $sp.getRecordValues(a, z, 'short_description,sys_id,number,sys_updated_on');
  if (z.short_description.nil())
    a.short_description = "(No description)";
  a.__table = z.getTableName();
  a.__page = "ticket";
  a.type = 'record';
 // a.sortOrder = z.sys_updated_on.getGlideObject().getNumericValue();
  t.items.push(a);
}

//Requests
var z = new GlideRecord('sc_req_item');
z.addQuery('opened_by', gs.getUserID());
z.orderByDesc('sys_updated_on');
z.query();
while (z.next()) {
  if (!z.canRead())
    continue;

  var a = {};
  $sp.getRecordValues(a, z, 'short_description,sys_id,number,sys_updated_on');
  if (z.short_description.nil())
    a.short_description = "(No description)";
  a.__table = z.getTableName();
  a.__page = "ticket";
  a.type = 'record';
 // a.sortOrder = z.sys_updated_on.getGlideObject().getNumericValue();
  t.items.push(a);
}

t.items.sort(function(a, b) {
  return b.sortOrder - a.sortOrder;
});

t.count = t.items.length;
t.items = t.items.splice(0, max+1); // only want first 30

var link = {title: gs.getMessage('View all requests'), type: 'link', href: '?id=my_requests', items: []};
t.items.unshift(link); // put 'View all requests' first
