sp_widget.client_script:
function($scope, $rootScope) {
  /* widget controller */
  var c = this;

	$scope.userID = $scope.user.sys_id;
	// Coleta informações sobre a foto do usuário logado
	$rootScope.$on('sp.avatar_changed', function() {
		$scope.userID = "";
		$timeout(function(){
			$scope.userID = $scope.user.sys_id;
		});
	});


}

