sp_widget.client_script:
function($scope, $rootScope, $timeout, categorySubscribe, $element, spUtil) {

	/* widget controller */

	var c = this;

	$scope.showWidget = "";
	var oldCategory = false;
	//Esperando o evento "showHideWidget" acontecer no GService Icon Link

	$('.content-category-items').css('display', 'block');

	c.data.showWidget = true;

	$rootScope.$on('showHideWidget', function(event,data) {

		if (categorySubscribe.getSelectedCategory()){
			oldCategory = categorySubscribe.getSelectedCategory();
		}

		if(data == "service"){

			if(categorySubscribe.getIconLink()){
				$timeout(function() {
					scrollTo("widget-category-items");
				});

				categorySubscribe.clearValues();
			}

			if(!categorySubscribe.getSelectedItem() && categorySubscribe.getCategoryID() != oldCategory){
				$timeout(function() {
					scrollTo("widget-category-items");
				});
				categorySubscribe.clearValues();	
			}

			categorySubscribe.clearValues();
		}
	});

	//Comunicando com o widget GService Categories para enviar o SysID da categoria selecionada

	$scope.selectedCategory = function(selection){
		c.selectedCategory = selection;
		categorySubscribe.setCategoryID(selection);
	};

	/*
	var lastScrollTop = 0;
	var container = angular.element('#widget-category-items');

	container.on("scroll", function() {
		var currentScroll = angular.element(container).scrollTop();
		if(lastScrollTop > currentScroll && lastScrollTop > 300 && currentScroll > 300) // moving up
			$element.show();
		lastScrollTop = currentScroll;
	});
	*/

}

