sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.showWidget = false;

	var category_property = gs.getProperty("categories_color");
	var parse = JSON.parse(category_property);

	var categories = options.selected_category.split(",");

	var allCategories = [];
	var grCategories = new GlideRecord("sc_category");
	for(var i=0; i<categories.length; i++){
		grCategories.get(categories[i])
		var categoryJSON = {title : grCategories.getValue("title"),
												description : grCategories.getValue("description"),
												sys_id : grCategories.getUniqueValue(),
												img : parse[categories[i]].imgcategory,
												color : parse[categories[i]].color
											 }

		allCategories.push(categoryJSON);
	}

	data.categories = allCategories;	


})();
