sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.parentid = options.parent_id;
	data.showWidget = false;
	data.title = options.title;

	//Coleta propriedade com imagens para selecionar a imagem de cada categoria
	var category_property = gs.getProperty("categories_color");
	var parse = JSON.parse(category_property);
	data.categoryImg = parse[options.selected_category].imgcategory;

	// Query para coletar as informações dos campos da categoria escolhida no option schema
	var gr = new GlideRecord('sc_category');
	gr.get(options.selected_category);
	var jsonCategory = {title : gr.getValue("title"),
											sys_id : gr.getUniqueValue(),
											description : gr.getValue("description")}
	data.category = jsonCategory;

})();
