sp_widget.client_script:
function() {
  var c = this;

	c.lastElement = false;

	c.back = function(){
		/*c.lastElement = true;
		$('.slide1').css('opacity', '1');
		$('.slide3').css('opacity', '0');
		$('.slide1').css('margin-left', '130px');*/
		c.data.action = "previousSlide";
		c.server.update();
	}

	c.next = function(){
		/*c.lastElement = true;
		$('.slide1').css('opacity', '0');
		$('.slide3').css('opacity', '1');
		$('.slide1').css('margin-left', '-122px');*/
		c.data.action = "nextSlide";
		c.server.update();
	}

	c.setOpacity = function(index){
		if(index == 0 || index == c.data.slides.length - 1){
			return "item_opacity";
		}
	}
}

