  	opacity: 0.2;
  }

}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var localInput = input;

	if(!localInput){
		data.slides = [{nome : "Slide 1"}, {nome : "Slide 2"}, {nome : "Slide 3"}, {nome : "Slide 4"}, {nome : "Slide 5"}, {nome : "Slide 6"}];
		data.slides.unshift(data.slides[data.slides.length - 1]);
	}
	else{
		switch(localInput.action){
			case "nextSlide":
				data.slides = localInput.slides;
				data.slides.shift();
				data.slides.push(data.slides[0]);
				break;
			case "previousSlide":
				data.slides = localInput.slides;
				data.slides.pop();
				data.slides.unshift(data.slides[data.slides.length - 1]);
				break;
			default:
				break;
		}
	}
})();
