sp_widget.client_script:
function($scope, $rootScope, $timeout, spUtil) {
	/* widget controller */
	var c = this;
	c.isLoading = true;

	$scope.loadingIndicator = $rootScope.loadingIndicator;
	//Show widget after loaded
	$scope.$on('sp_loading_indicator', function(e, value) {
		if($scope.loadingIndicator && !value){
			$timeout(function(){
				c.isLoading = false;
			}, 200);
		}
		$scope.loadingIndicator = value;
	});

	spUtil.recordWatch($scope, "wm_order", c.data.equipment_query, function(event) {
		c.server.get({
			action: "equipmentsCount"
		}).then(function(response) {
			c.data.equipmentsCount = response.data.equipmentsCount;
		});
	});

	$scope.userID = $scope.user.sys_id;
	//Get user picture
	$rootScope.$on('sp.avatar_changed', function() {
		$scope.userID = "";
		$timeout(function(){
			$scope.userID = $scope.user.sys_id;
		});
	});

	c.selectTab = function(tab, index){
		c.isLoading = true;
		c.index = index;
		c.isLoading = true;

		for(var i in c.data.tabs){
			if(c.data.tabs[i].name == tab.name){
				c.data.tabs[i].selected = true;
			}
			else{
				c.data.tabs[i].selected = false;
			}
		}
		c.server.get({
			action: "selectedTab",
			widgets: c.data.widgets,
			index: index,
			tabName : tab.name
		}).then(function(response) {
			c.data.widgets = response.data.widgets;
		});
	}

}

