sp_widget.link:
function(scope, elem) {
	$(elem).on('keydown','.header-menu-item',function(e){
		var target = e.target;
		if (target.localName == 'a') {
			var currentMenuItem = $(target).parents('li');
			var parentUL = currentMenuItem.parents('ul');
			var allMenuItems = parentUL.children('li:visible');
			var firstMenuItem = allMenuItems.first();
			var lastMenuItem = allMenuItems.last();
			var menuItemToFocus;
			if(e.keyCode == 37) {
				e.preventDefault();
				currentMenuItem.removeClass('open');
				var previousMenuItem = currentMenuItem.prevAll('li:visible').first();
				//enable circular navigation
				menuItemToFocus = previousMenuItem.length ? previousMenuItem : lastMenuItem;
				menuItemToFocus.find('a').focus();
			} else if(e.keyCode == 39) {
				e.preventDefault();
				currentMenuItem.removeClass('open');
				var nextMenuItem = currentMenuItem.nextAll('li:visible').first();
				//enable circular navigation
				menuItemToFocus = nextMenuItem.length ? nextMenuItem : firstMenuItem;
				menuItemToFocus.find('a').focus();
			}
			if(e.keyCode == 9) {
				$(target).parents('li').removeClass('open');
			}
		}
		});
	}

sp_widget.script:
(function(){
	var localInput = input;
	var portalUtils = new global.OCLPortalUtils();
	var equipmentsUtils = new global.OCLPortalEquipmentsUtil();

	var delegateUsersNotification = [];
	var delegateUsersApproval = [];
	var grDelegate = new GlideRecord("sys_user_delegate");
	grDelegate.addQuery("delegate", gs.getUserID());
	var qc = grDelegate.addQuery("u_pending_requests", true);
	qc.addOrCondition("approvals", true);
	grDelegate.query();
	if(grDelegate.next()){
		do{
			if(grDelegate.getValue("u_pending_requests") == 1){
				delegateUsersNotification.push(grDelegate.getValue("user"));
			}
			if(grDelegate.getValue("approvals") == 1){
				delegateUsersApproval.push(grDelegate.getValue("user"));
			}
		} while(grDelegate.next());
	}


	data.taskQuery = "state=-5^u_pending_reason=1^request_item.opened_by.sys_idIN"+ gs.getUserID();
	data.incidentQuery = "state=3^hold_reason=1^opened_by.sys_idIN" + gs.getUserID();
	data.equipment_query = gs.getProperty("gservice_equipments_query");
	data.approverQuery = "state=requested^source_table=sc_req_item^ORsource_table=change_request^approver.sys_idIN" + gs.getUserID();

	if(delegateUsersNotification.length > 0){
		data.taskQuery += "," + delegateUsersNotification.join(",");
		data.incidentQuery += "," + delegateUsersNotification.join(",");
	}
	if(delegateUsersApproval.length > 0){
		data.approverQuery += "," + delegateUsersApproval.join(",");
	}

	data.dashboardCount = portalUtils.setDashboardCount(data.taskQuery, data.incidentQuery, data.approverQuery);
	data.equipmentsCount = equipmentsUtils.getEquipmentsCount();
	if(localInput){
		if(localInput.action == "dashboardCount"){
			data.dashboardCount = portalUtils.setDashboardCount(localInput.taskQuery, localInput.incidentQuery, localInput.approverQuery);
		}
		else if(localInput.action == "equipmentsCount"){
			data.equipmentsCount = equipmentsUtils.getEquipmentsCount();
		}
	}

	var gestorGroup = gs.getProperty("gservice_gestor_page_group");
	data.isManager = gs.getUser().isMemberOf(gestorGroup);
	data.isAdmin = gs.getUser().hasRole('admin');
	data.showEquipments = false;
	if(data.isAdmin || gs.getUser().hasRole('wm_initiator') || gs.getUser().hasRole('wm_initiator_qualifier')){
		data.showEquipments = true;
	}

	// build menus
	var menu_id = $sp.getValue('sys_id'); // instance sys_id
	var gr = new GlideRecord('sp_instance_menu');
	gr.get(menu_id);
	data.menu = {};
	data.menu.items = $sp.getMenuItems(menu_id);
	data.isLoggedIn = GlideSession.get().isLoggedIn();
	// Show tours only if gtd's sp sys property is set
	data.showTours = (gs.getProperty('com.snc.guided_tours.sp.enable') === 'true') && data.isLoggedIn;

	var catalogArr = ($sp.getCatalogs().value + "").split(",");

	catalogArr.forEach(function(catalog) {
		data.showWishlist = data.showWishlist || new sn_sc.Catalog(catalog).isWishlistEnabled();
	});
	if (data.isLoggedIn) {
		if (data.showWishlist)
			data.wishlistWidget = $sp.getWidget("sc_wishlist_cart", {wishlistTemplate: "small_wishlist.html", auto_update_wishlist:options.auto_update_wishlist});
		if (gs.getProperty("glide.sc.portal.use_cart_v2_header", "false") === "true")
			data.cartWidget = $sp.getWidget("sc-shopping-cart-v2", {cartTemplate: "small_shopping_cart_v2.html", auto_update_cart:options.auto_update_cart});
		else
			data.cartWidget = $sp.getWidget("sc-shopping-cart", {cartTemplate: "small_shopping_cart.html", auto_update_cart:options.auto_update_cart});
	}


})();
