sp_widget.client_script:
function($scope,$rootScope,categorySubscribe,$location) {
  var c = this;

	c.openDropDown = function(item){
		cleanSelection("clearAll");
		if(item == c.data.menuSelected && c.data.menuSelected != 2){
			c.data.menuSelected = 0;
		}
		else{
			c.data.menuSelected = item;

			if(item == 2){
				$rootScope.$broadcast('showHideWidget', "error");
			}
			else if(item == 3){
				c.data.showItems = false;
				c.data.thirdCategories = false;

				for(var i in c.data.categories){
					for(var j in c.data.categories[i].categories){
						c.data.categories[i].categories[j].selected = false;
					}
				}
			}
		}
	}

	c.goPage = function(page) {
		$location.url(page);
	}

	c.selectPill2 = function(item){
		categorySubscribe.setIconLink();
		$rootScope.$broadcast('showHideWidget', item);
	};

}

sp_widget.css:
.widget-gservice-icons-mobile {
  display: flex;
  flex-wrap: wrap;
  padding: 10px;
  background-color: white;

  div {
    transition: .2s ease-in-out;
    padding: 10px;
    width: 50%;

    img { width: 100%; }

    &:hover {
      transition: .1s ease-in-out;
      opacity: .7;
    }
  }
}

@media only screen and (min-width: 768px) {
  .widget-gservice-icons-mobile {
    display: none;
  }
}
