sp_widget.client_script:
function($rootScope,$scope, categorySubscribe) {

	// Função encontrada no Community: https://community.servicenow.com/community?id=community_blog&sys_id=e60eea2ddbd0dbc01dcaf3231f961972

	/* widget controller */

	var c = this;

	c.selectedPill = c.data.idname; // Quando a página carrega, não seleciona nenhum widget para exibir

	//Exibe a seleção sempre que selecionado cada valor

	$scope.selectPill = function(){
		categorySubscribe.setIconLink();
		$rootScope.$broadcast('showHideWidget', c.data.idname);
	};

	$scope.$on('openMenu', function (event, data) {
		/*if(data == c.data.idname){
			$rootScope.$broadcast('showHideWidget', c.data.idname);
		}*/
		//Utilizado para aparecer o widget de serviços
	});

}

