sp_widget.client_script:
function($rootScope,$scope, categorySubscribe) {
	var c = this;

	c.selectedPill = c.data.idname;

	$scope.selectPill = function(){
		categorySubscribe.setIconLink();
		$rootScope.$broadcast('showHideWidget', c.data.idname);
	};

}

