sp_widget.client_script:
function($window) {
  /* widget controller */
  var c = this;

	c.redirectPA = function(){
		$window.open(c.data.instance_url,'_blank');
	}

	c.redirectPA2 = function(){
		$window.open(c.data.instance_url2,'_blank');
	}

	c.redirectPA3 = function(){
		$window.open(c.data.instance_url3,'_blank');
	}
}

