	data.instance_url2 = gs.getProperty('glide.servlet.uri') + "nav_to.do?uri=%2F$pa_dashboard.do%3Fsysparm_dashboard="+gs.getProperty('x_gmeps_itsm_servi.ocl.gservice_panels.instance_url2');

	data.instance_url3 = gs.getProperty('glide.servlet.uri') + "nav_to.do?uri=$pa_dashboard.do%3Fsysparm_dashboard%3De7aa34ac1bcd60102f9887f1f54bcbab%26sysparm_tab%3De17b7c201b01a0102f9887f1f54bcbef%26sysparm_cancelable%3Dtrue%26sysparm_editable%3Dundefined%26sysparm_active_panel%3Dfalse";
