sys_script_include.script:
var VariableUtil = Class.create();
VariableUtil.prototype = {
    initialize: function() {},
    getVsetFields: function(vsets, exceptions) {
        var fields = [];

        // type is not container start, split, end and label
        var query = 'type!=19^type!=20^type!=24^type!=11^variable_set.sys_idIN';

        for (var i = 0; i < vsets.length; ++i) {
            query += vsets[i] + ',';
        }

        var vsetGR = new GlideRecord('item_option_new');
        vsetGR.addEncodedQuery(query);
        vsetGR.addQuery('active', 'true');
        vsetGR.query();

        while (vsetGR.next()) {
            var name = vsetGR.name.toString();
            if (exceptions.indexOf(name) == -1) {
                fields.push(name);
            }
        }

        return fields;
    },
    type: 'VariableUtil'
};
